import React, { useState, useEffect, useContext } from "react";
import ROUTES from "./Routes";
import { Route, Redirect } from "react-router-dom";

import { UserContext } from "../utils/UserProvider";
import Todos from "../pages/Todos";

const PrivateRoute = (props) => {
    const { username, token } = useContext(UserContext);

    const [auth, setAuth] = useState({username: undefined, token: undefined});

    useEffect(() => {
        if(username && token) {
            console.log("PRIVATENAME: " + username);
            console.log("PRIVATE TOKEN: " + token);
            setAuth({ username, token });
            console.log("PRIVATE AUTH: " + auth);
        }
    }, [username, token]);

    const isAuthenticated = () => {
        auth.username != undefined && auth.token != undefined
    };

    return (
        <Route {...props} render={props => 
            isAuthenticated() ? (<Route exact path={ROUTES.TODOLIST}><Todos /></Route>)
                            : (<Redirect to={ROUTES.LOGIN} />)
        } />
    );
}

export default PrivateRoute;
